package test

import (
	"github.com/advancevillage/3rd/logs"
	"testing"
)

func TestTxtLogger_Error(t *testing.T) {
	logger, err := logs.NewTxtLogger("error.log", 1024, 2)
	if err != nil {
		t.Error(err.Error())
	}
	for i := 0; i < 5000; i++ {
		str := ""
		logger.Emergency(str)
	}
}
